"use strict";(self.webpackChunksingulatron_api_docs=self.webpackChunksingulatron_api_docs||[]).push([[998],{45448:e=>{e.exports=JSON.parse('{"tag":{"label":"services","permalink":"/docs/tags/services","allTagsPath":"/docs/tags","count":7,"items":[{"id":"services/chat-svc","title":"Chat Svc","description":"The chat service keeps a database of threads, messages and file assets associated with them. Chat messages are the primary user interface of LLMs and other AI architectures.","permalink":"/docs/services/chat-svc"},{"id":"services/deploy-svc","title":"Deploy Svc","description":"The deploy service is responsible of launching containers on whatever infrastructure the Superplatform is running on (eg. Docker Svc) and registering them into the Registry Svc.","permalink":"/docs/services/deploy-svc"},{"id":"services/docker-svc","title":"Docker Svc","description":"The docker service maintains containers on a node. It expects the docker socket to be mounted.","permalink":"/docs/services/docker-svc"},{"id":"services/download-svc","title":"Download Svc","description":"The download service keeps a network local copy of files frequently accessed by services in the Singulatron platform.","permalink":"/docs/services/download-svc"},{"id":"services/dynamic-svc","title":"Dynamic Svc","description":"The dynamic service is designed to help build backendless applications: the goal is to be able to save and query data directly from the frontend. Similarly to Firebase.","permalink":"/docs/services/dynamic-svc"},{"id":"services/model-svc","title":"Model Svc","description":"The model service can start, stop AI models across multiple runtimes (eg. Docker) and maintains a database of available models on the platform.","permalink":"/docs/services/model-svc"},{"id":"services/prompt-svc","title":"Prompt Svc","description":"The prompt service provides an easy to use interface to prompt LLMs and use AI models. Aims to serve humans and machines alike with its resilient queue based architecture.","permalink":"/docs/services/prompt-svc"}],"unlisted":false}}')}}]);